INSERT INTO `wpLLV_rg_lead_detail` (`id`, `lead_id`, `form_id`, `field_number`, `value`) VALUES
(10, 4, 1, 4, 'Sigríður Axelsdóttir'),
(11, 4, 1, 3, 'saxelsdottir@hotmail.com'),
(12, 4, 1, 6, '(354)864-3505'),
(13, 4, 1, 2, 'Óska eftir Whey banana próteini'),
(14, 5, 1, 4, 'Ingunn Lúðvíksdóttir'),
(15, 5, 1, 3, 'ingunn@crossfitsport.is'),
(16, 5, 1, 2, 'Góðan daginn,\r\nIngunn heiti ég, kem reglulega og kaupi hjá ykkur vörur, og þið gáfuð okkur einmitt tvo vinninga í móti sem við héldum innanhús áður en við fórum á Evrópumótið. Helgina 16-18. maí sl. v'),
(17, 6, 1, 4, 'Bergrún'),
(18, 6, 1, 3, 'beri@mail.holar.is'),
(19, 6, 1, 2, 'Góðan daginn,\r\nÉg er búin að vera að skoða brennslutöflunar ykkar rippedcore og langar aðeins að spurja frekar út í þær.\r\nMér finnst ég hafa staðnað í þjálfuninni og sé lítinn sem engan mun þessa daga'),
(20, 7, 1, 4, 'Steinar Sörensson'),
(21, 7, 1, 3, 's.sorens@visir.is'),
(22, 7, 1, 6, '(354)697-5441'),
(23, 7, 1, 2, 'byrjaði að versla við ykkur í síðasta mánuði, mjög ánægður með vörurnar, preworkoutið er eitthvað það allra besta sem ég hef prófað, það sem ég er að velta fyrir mér er að ég er í netklubbnum hjá ykku'),
(24, 8, 1, 4, 'Eiríkur Þorri Einarsson'),
(25, 8, 1, 3, 'eirikur.einarsson@gmail.com'),
(26, 8, 1, 2, 'Góðan dag.  Ég hef mikinn áhuga á að vera tekinn af sms listanum ykkar. \r\nSíminn minn er 865-8700\r\n\r\nKv Eiríkur Þorri'),
(27, 9, 1, 4, 'Ína Dóra'),
(28, 9, 1, 3, 'inadorah@gmail.com'),
(29, 9, 1, 2, 'Var að panta hjá ykkur en gengur illa að greiða fyrir vöruna.\r\nEru þetta ekki réttar upplýsingar?\r\n\r\nKt:  430605 1190\r\nBanki 331 26 1190\r\n\r\nFæ alltaf villumeldingu um að kennitala og reikningsnúmer pa'),
(30, 10, 1, 4, 'Berglind Bjarnadóttir'),
(31, 10, 1, 3, 'hrutafel@simnet.is'),
(32, 10, 1, 2, 'Hæ mig langar til að skoða hvað þið hafið uppá að bjóða'),
(33, 11, 1, 4, 'Anna'),
(34, 11, 1, 3, 'Ahallgrims@gmail.com'),
(35, 11, 1, 2, 'Hæ, eg er algjör nýgræðingur í svona bæti efnum, mer finnst vanta  skýringar með hverri vörutegund, hvað hun inniheldur, fyrir hvern hentar hún ofl ofl\r\nBestu kveðjur anna'),
(36, 12, 1, 4, 'Karolína Árnadóttir'),
(37, 12, 1, 3, 'karolina10@simnet.is'),
(38, 12, 1, 2, 'Góðan daginn\r\n\r\nÉg keypti þessa vöru, http://sci-mx.is/fitubrennsla/shred-x-rippedcore , hjá ykkur og borgaði 21. okt. fékk hana senda til mín og tók á móti henni 23. okt. Byrjaði á að notað hana stra'),
(39, 13, 1, 4, 'Guðjón Einar Guðmundsson'),
(40, 13, 1, 3, 'gudjong@shs.is'),
(41, 13, 1, 6, '(354)867-1009'),
(42, 13, 1, 2, 'Mig langaði að forvitnast smávegis, ég er aðeins í grennri kantinum, og langar að skoða svona prótein eða eitthvað til að reyna þyngja mig aðeins og byggja upp smá vöðvamassa. Með hverju mæliði hjá yk'),
(43, 14, 1, 4, 'Júlía Sjörup Eiríksdóttir'),
(44, 14, 1, 3, 'julia@tplus.is'),
(45, 14, 1, 2, 'Sæl, \r\n\r\nÉg sé að númerið mitt er á smslista hjá ykkur. Það er búið að skipta um eiganda á þessu númeri 895-3921, þið megið vinsamlegst taka það af lista hjá ykkur.\r\n\r\nBestu kveðjur, \r\nJúlía Sjørup'),
(46, 15, 1, 4, 'Helen'),
(47, 15, 1, 3, 'helenomars@gmail.com'),
(48, 15, 1, 2, 'Góðan dag\r\n\r\nViljið þið taka mig út af sms fréttalistanum ykkar. Númerið er 864-7814\r\n\r\nTakk'),
